import React from 'react';
import styled from 'styled-components';

const Counter = styled.div`
    font-size: 32px;
    justify-content: center;
    align-items: center;
    display:flex;
`;

export default (props) => {

    let {
        className = '',
        counter = 0
    } = props;

    return (
        <Counter className={ className }>
            { counter }
        </Counter>
    );
}